DROP PROCEDURE sp_find_sum;GO/* Procedure Code */CREATE PROCEDURE sp_find_sum--ALTER PROCEDURE sp_find_sum    @first_number INT,    @second_number INTASBEGIN    DECLARE @sum INT;    SET @sum = @first_number + @second_number;    Print 'The sum is ' + str(@sum);END/* To run the procedure */EXECUTE sp_find_sum 10,20;EXECUTE sp_find_sum 2,50;EXECUTE sp_find_sum @first_number=2,@second_number=50;EXECUTE sp_find_sum @second_number=50,@first_number=2;GOCREATE PROCEDURE sp_find_max    @first_number INT,    @second_number INTASBEGIN    DECLARE @MAX INT;    IF @first_number > @second_number    BEGIN        SET @MAX = @first_number;    END    ELSE     BEGIN        SET @MAX = @second_number;    END    Print 'Max number is ' + STR(@MAX);END GOEXECUTE sp_find_max 10,50;EXECUTE sp_find_max 50,10;GOCREATE PROCEDURE sp_print_natural    @N INTASBEGIN    DECLARE @I INT;    SET @I = 1;    WHILE @I <= @N    BEGIN        Print STR(@I);        SET @I = @I + 1;    END ENDGOEXECUTE sp_print_natural 15;GO-------------------------------------------------------------------------------------CREATE PROCEDURE sp_create_booking    @username VARCHAR(50),    @flight_no VARCHAR(50),    @booking_id INT,    @booking_number VARCHAR(50),    @number_of_passengers INTASBEGIN    DECLARE @vcustomer_id INT;    DECLARE @vflight_id INT;    DECLARE @vtravel_date DATETIME;    DECLARE @vfare FLOAT;    DECLARE @vbooking_amount FLOAT;        SELECT @vcustomer_id=customer_id     FROM customer    WHERE username=@username;        SELECT @vflight_id=flight_id,        @vtravel_date=travel_date,        @vfare = ticket_fare    FROM flight    WHERE flight_no=@flight_no;    SET @vbooking_amount = @vfare * @number_of_passengers;   /* Print STR(@vcustomer_id) + ',' + STR(@vflight_id)         + ',' + FORMAT(@vtravel_date,'dd-MM-yyyy');*/        INSERT INTO booking(booking_id,booking_date,booking_number,        customer_id,flight_id,        number_of_passengers,travel_date,total_fare)    VALUES(@booking_id,getdate(),@booking_number,        @vcustomer_id,@vflight_id,        @number_of_passengers,@vtravel_date,@vbooking_amount);ENDSELECT * FROM CUSTOMER;SELECT * FROM FLIGHT;SELECT * FROM booking;SP_HELP BOOKING;EXECUTE sp_create_booking 'ashish','AI 302',10014,'005',2;GO---------------------------------------------------------------------------------------CREATE PROCEDURE sp_change_fare     @flight_id INT,    @fare FLOATASBEGIN    BEGIN TRANSACTION;    UPDATE flight     SET ticket_fare=@fare    WHERE flight_id=@flight_id;    COMMIT TRANSACTION;ENDGOEXEC sp_change_fare 3,11000;select * from flight;GO-------------------------------------------------------------------------CREATE PROCEDURE sp_search_flights    @search_text VARCHAR(50)ASBEGIN    SELECT *     FROM flight     WHERE (source_point LIKE ('%' + @search_text + '%'))        OR         (destination_point LIKE ('%' + @search_text + '%'));ENDGOEXECUTE sp_search_flights 'DELHI'